import React, { useState } from "react";
import "./homeStyle.css";

function Home() {
  const accountDummy = [
    {
      id: 0,
      accNumb: 1234567,
      balance: 150000000,
      lastTransaction: 22042021,
    },
    {
      id: 1,
      accNumb: 1234568,
      balance: 200000000,
      lastTransaction: 25032021,
    },
    {
      id: 3,
      accNumb: 1234569,
      balance: 80000000,
      lastTransaction: 18032021,
    },
    {
      id: 4,
      accNumb: 1234570,
      balance: 500000000,
      lastTransaction: 14022021,
    },
    {
      id: 5,
      accNumb: 1234571,
      balance: 700000000,
      lastTransaction: 28042021,
    },
  ];

  const [clickAcc, setClickAcc] = useState(accountDummy);
  return (
    <div className="home">
      <div className="listContainer">
        <h2>Account List</h2>
        <button>
          {clickAcc.map((value) => {
            return (
              <div className="card" key={value.id}>
                <div className="accNumber">{value.accNumb}</div>
                <div className="lowerSectCard">
                  <div>
                    Rp.
                    {value.balance
                      .toString()
                      .replace(/\B(?<!\.\d*)(?=(\d{3})+(?!\d))/g, ",")}
                    ,-
                  </div>
                  <div>{value.lastTransaction}</div>
                </div>
              </div>
            );
          })}
        </button>
      </div>
    </div>
  );
}

export default Home;
